mapper.select:
select buildingname, sido, sigungu, bname, bcode,lat,lng,userid
from company
where companyno = #{companyno}

mapper.update:
update company
set buildingname
= #{buildingname}, sido = #{sido}
, sigungu = #{sigungu}
, bname = #{bname}
, bcode = #{bcode}
, lat = #{lat} 
where companyno = #{companyno}
